	class AtsrState {

	public:

		int32_t attackIncrement = 0;
		int32_t tIncrement = 0;
		int32_t releaseIncrement = 0;

		int32_t aLevel = 0;
		int32_t bLevel = 0;
		int32_t aScale = 0;
		int32_t bScale = 0;

		int32_t phase = 0;

		virtual void step() {}
		virtual void processGateHigh() {}
		virtual void processGateLow() {}

	};

	AtsrState * atsrState;

	class Resting: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase = 0;
			aLevel = 0;
			bLevel = 0;
		}
		void processGateHigh(void) override {
			module.atsrState = &module.attack;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = 0;
			module.atsrState->bLevel = 0;
			module.attacking = 1;
		}

		/// Define the pointer to the outer class.
		Resting(ViaAtsr& x): module(x) {}

	};

	class Attack: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase += attackIncrement;
			if (phase >= 0xFFFFFFF) {
				phase = 0xFFFFFFF;
				module.atsrState = &module.t;
				module.atsrState->phase = 0;
				module.atsrState->aLevel = 65535;
				module.atsrState->bLevel = 0;
				module.attacking = 0;
				module.ting = 1;
			} else {
				aLevel = module.evaluateA(phase);
				bLevel = 0;
			}

		}
		void processGateLow(void) override {
			module.atsrState = &module.releaseFromA;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = aLevel;
			module.atsrState->aScale = aLevel;
			module.atsrState->bLevel = 0;
			if (!module.gateDelayActive) {
				module.gateDelayActive = 1;
				module.gateDelayPhase = phase;
			}
			module.attacking = 0;
			module.releasing = 1;
		}

		/// Define the pointer to the outer class.
		Attack(ViaAtsr& x): module(x) {}

	};

	class T: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase += tIncrement;
			if (phase > 0xFFFFFFF) {
				phase = 0xFFFFFFF;
				module.atsrState = &module.sustain;
				module.atsrState->phase = 0;
				module.atsrState->aLevel = 0;
				module.atsrState->bLevel = 65535;
				module.ting = 0;
				module.sustaining = 1;
			} else {
				aLevel = module.evaluateD(MAX_PHASE - phase);
				bLevel = 65535 - aLevel;
			}

		}

		void processGateHigh(void) override {
			module.atsrState = &module.retrigger;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = aLevel;
			module.atsrState->bLevel = bLevel;
			module.atsrState->aScale = aLevel;
			module.atsrState->bScale = bLevel;
			module.ting = 0;
			module.attacking = 1;
		}

		void processGateLow(void) override {
			module.atsrState = &module.releaseFromT;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = aLevel;
			module.atsrState->aScale = aLevel;
			module.atsrState->bScale = bLevel;
			module.atsrState->bLevel = bLevel;
			if (!module.gateDelayActive) {
				module.gateDelayActive = 1;
				module.gateDelayPhase = phase + (1 << 28);
			}
			module.ting = 0;
			module.releasing = 1;
		}

		/// Define the pointer to the outer class.
		T(ViaAtsr& x): module(x) {}

	};

	class Sustain: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			aLevel = 0;
			bLevel = 65535;
		}

		void processGateHigh(void) override {
			module.atsrState = &module.retrigger;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = 0;
			module.atsrState->bLevel = 65535;
			module.atsrState->aScale = 0;
			module.atsrState->bScale = 65535;
			module.sustaining = 0;
			module.attacking = 1;
		}

		void processGateLow(void) override {
			module.atsrState = &module.releaseFromS;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = 0;
			module.atsrState->bLevel = 65535;
			module.sustaining = 0;
			module.releasing = 1;
		}

		/// Define the pointer to the outer class.
		Sustain(ViaAtsr& x): module(x) {}

	};

	class ReleaseFromA: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase += releaseIncrement;
			if (phase > 0xFFFFFFF) {
				phase = 0xFFFFFFF;
				module.atsrState = &module.resting;
				module.atsrState->phase = 0;
				module.atsrState->aLevel = 0;
				module.atsrState->bLevel = 0;
				module.releasing = 0;
			} else {
				aLevel = fix16_mul(aScale, module.evaluateR(MAX_PHASE - phase));
				bLevel = 0;
			}
		}
		void processGateHigh(void) override {
			module.atsrState = &module.retrigger;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = aLevel;
			module.atsrState->bLevel = 0;
			module.atsrState->aScale = aLevel;
			module.atsrState->bScale = 0;
			module.releasing = 0;
			module.attacking = 1;
		}

		/// Define the pointer to the outer class.
		ReleaseFromA(ViaAtsr& x): module(x) {}

	};

	class ReleaseFromT: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase += releaseIncrement;
			if (phase > 0xFFFFFFF) {
				phase = 0xFFFFFFF;
				module.atsrState = &module.resting;
				module.atsrState->phase = 0;
				module.atsrState->aLevel = 0;
				module.atsrState->bLevel = 0;
				module.releasing = 0;
			} else {
				aLevel = fix16_mul(aScale, module.evaluateR(MAX_PHASE - phase));
				bLevel = fix16_mul(bScale, module.evaluateR(MAX_PHASE - phase));
			}
		}
		void processGateHigh(void) override {
			module.atsrState = &module.retrigger;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = aLevel;
			module.atsrState->bLevel = bLevel;
			module.atsrState->aScale = aLevel;
			module.atsrState->bScale = bLevel;
			module.releasing = 0;
			module.attacking = 1;
		}

		/// Define the pointer to the outer class.
		ReleaseFromT(ViaAtsr& x): module(x) {}

	};

	class ReleaseFromS: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase += releaseIncrement;
			if (phase > 0xFFFFFFF) {
				phase = 0xFFFFFFF;
				module.atsrState = &module.resting;
				module.atsrState->phase = 0;
				module.atsrState->aLevel = 0;
				module.atsrState->bLevel = 0;
				module.releasing = 0;
			} else {
				aLevel = 0;
				bLevel = module.evaluateR(MAX_PHASE - phase);
			}
		}
		void processGateHigh(void) override {
			module.atsrState = &module.retrigger;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = 0;
			module.atsrState->bLevel = bLevel;
			module.atsrState->aScale = 0;
			module.atsrState->bScale = bLevel;
			module.releasing = 0;
			module.attacking = 1;
		}

		/// Define the pointer to the outer class.
		ReleaseFromS(ViaAtsr& x): module(x) {}

	};

	class Retrigger: public AtsrState {

	public:

		ViaAtsr& module;

		void step(void) override {
			phase += attackIncrement;
			if (phase > 0xFFFFFFF) {
				phase = 0xFFFFFFF;
				module.atsrState = &module.t;
				module.atsrState->phase = 0;
				module.atsrState->aLevel = 65535;
				module.atsrState->bLevel = 0;
				module.attacking = 0;
				module.ting = 1;
			} else {
				int32_t position = module.evaluateA(phase);
				aLevel = aScale + fix16_mul(65535 - aScale, module.evaluateA(phase));
				bLevel = fix16_mul(bScale, 65535 - position);
			}
		}
		void processGateLow(void) override {
			module.atsrState = &module.releaseFromT;
			module.atsrState->phase = 0;
			module.atsrState->aLevel = aLevel;
			module.atsrState->aScale = aLevel;
			module.atsrState->bLevel = bLevel;
			module.atsrState->bScale = bLevel;
			module.attacking = 0;
			module.releasing = 1;
		}

		/// Define the pointer to the outer class.
		Retrigger(ViaAtsr& x): module(x) {}

	};

